    public RedisTemplate<Object, Object> redisTemplate(LettuceConnectionFactory lettuceConnectionFactory) {
        RedisTemplate<Object, Object> template = new RedisTemplate<>();
        template.setConnectionFactory(lettuceConnectionFactory);
        template.setValueSerializer(new com.alibaba.fastjson2.JSONSerializer());
        template.setKeySerializer(new StringRedisSerializer());
        return template;
    }

    @Bean
    public RedissonClient redisson(RedisProperties redisProperties) {
        Config config = new Config();
        config.useSingleServer().setAddress(redisProperties.getHost() + ":" + redisProperties.getPort());
        return Redisson.create(config);
    }

    @Bean
    public KeyGenerator keyGenerator() {
        return new KeyGenerator() {
            @Override
            public String generateKey(Method method, Object... params) {
                StringBuilder sb = new StringBuilder();
                sb.append(method.getDeclaringClass().getName())
                  .append(":")
                  .append(method.getName())
                  .append("(")
                  .append(String.join(",", Arrays.stream(params).map(Object::toString).toArray(String[]::new)))
                  .append(")");
                return DigestUtils.sha256Hex(sb.toString());
            }
        };
    }
