      out.write(ByteBuffer.wrap(directories.leaves));
      out.position(0);
      out.write(ByteBuffer.wrap(header.toBytes()));
      out.write(ByteBuffer.wrap(directories.root));
    } catch (IOException e) {
      LOGGER.error(e.getMessage());
    }
  }

