                        OutputStream outputStream = newSocket.getOutputStream();

                        outputStream.write(req.getBytes());
                        outputStream.flush();

                        return IOUtils.analysisStream(inputStream);
                    }
                };

                futureTask.cancel(true);
                futureTask = new FutureTask<>(callable);
                futureTask.run();

                try {
                    String result = futureTask.get(30, TimeUnit.SECONDS);
                    if (StringUtils.contains(result, "Requested Range Not Satisfiable")) {
                        addVulnerable();
                    }
                } catch (Exception ee) {
                } finally {
                    futureTask.cancel(true);
                    newSocket.close();
                }
            } finally {
                socket.close();
            }
        } catch (Exception e) {
            SysLog.error(e);
        }
    }

    private void addVulnerable() {
