    private void callback(long apiCallId, long retCode, String message, String cause, String correction, String details, Map<String, String> objRefsMap) {
        synchronized (CALLBACK_LOCK) {
            for (MessageCallback callback : callbacks) {
                callback.onMessageReceived(apiCallId, retCode, message, cause, correction, details, objRefsMap);
            }
        }
    }

    private class ProtobufIOWorker implements Runnable {
        private String threadName;

        public ProtobufIOWorker(String threadName) {
            this.threadName = threadName;
        }

        @Override
        public void run() {
            while (!shutdown) {
                try {
                    byte[] lenHeader = new byte[4];
                    int bytesRead = inputStream.read(lenHeader);
                    if (bytesRead < 4) continue;
                    int len = ((lenHeader[0] & 0xFF) << 24) |
                              ((lenHeader[1] & 0xFF) << 16) |
                              ((lenHeader[2] & 0xFF) << 8) |
                              (lenHeader[3] & 0xFF);
                    byte[] data = new byte[len];
                    bytesRead = inputStream.read(data);
                    if (bytesRead < len) continue;
                    MessageLite message = MessageLite.parseFrom(data);
                    callback(message.getId(), message.getRetCode(), message.getMessage(), message.getCause(), message.getCorrection(), message.getDetails(), message.getObjRefsMap());
                } catch (IOException | IllegalArgumentException e) {
                    outStream.println("Error reading from input: " + e.getMessage());
                }
            }
        }
    }
}

