abstract class Connection {
    protected final int id;
    protected final Client client;
    protected final Selector selector;
    protected final IPv4Header ipv4Header;
    protected final TCPHeader tcpHeader;
    protected final Queue<IPv4Packet> packets = new LinkedList<>();
    protected final AtomicBoolean running = new AtomicBoolean(true);

    public Connection(ConnectionId id, Client client, Selector selector, IPv4Header ipv4Header, TCPHeader tcpHeader) {
        this.id = id.getId();
        this.client = client;
        this.selector = selector;
        this.ipv4Header = ipv4Header;
        this.tcpHeader = tcpHeader;
    }

    public void disconnect() {
        running.set(false);
        try {
            if (channel != null) {
                channel.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    protected abstract void processReceive();
    protected abstract void processSend();
    protected abstract void sendToNetwork(IPv4Packet packet);
    protected abstract void updateInterests();
    protected abstract boolean mayRead();
    protected abstract boolean mayWrite();
    protected abstract boolean mayConnect();
}

class TCPConnection extends Connection {
    private final int MTU = 16384;
    private final int MAX_PAYLOAD_SIZE = MTU - 40; // Assuming 40 bytes for headers
    private final StreamBuffer clientToNetwork = new StreamBuffer(MAX_PAYLOAD_SIZE);
    private final Packetizer networkToClient = new Packetizer();
    private IPv4Packet packetForClient;
    private final SocketChannel channel;
    private final SelectionKey selectionKey;
    private final int interests;

    public TCPConnection(ConnectionId id, Client client, Selector selector, IPv4Header ipv4Header, TCPHeader tcpHeader) {
        super(id, client, selector, ipv4Header, tcpHeader);
        this.channel = createChannel();
        this.selectionKey = channel.register(selector, 0);
        this.interests = 0;
        updateInterests();
    }

    @Override
    public void disconnect() {
        super.disconnect();
        if (selectionKey != null) {
            selectionKey.cancel();
        }
    }

    @Override
    public void processReceive() {
        try {
            int bytesRead = channel.read(networkToClient.getBuffer());
            if (bytesRead > 0) {
                networkToClient.processData();
                packetForClient = networkToClient.getNextPacket();
                if (packetForClient != null) {
                    client.processPacket(packetForClient);
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void processSend() {
        try {
            if (!clientToNetwork.isEmpty()) {
                ByteBuffer buffer = clientToNetwork.getBuffer();
                while (buffer.hasRemaining()) {
                    int bytesSent = channel.write(buffer);
                    if (bytesSent == 0) break;
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void sendToNetwork(IPv4Packet packet) {
        try {
            ByteBuffer buffer = ByteBuffer.allocate(packet.getSize());
            packet.serialize(buffer);
            clientToNetwork.write(buffer);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void updateInterests() {
        if (mayRead()) {
            interests |= SelectionKey.OP_READ;
        }
        if (mayWrite()) {
            interests |= SelectionKey.OP_WRITE;
        }
        if (mayConnect()) {
            interests |= SelectionKey.OP_CONNECT;
        }
        selectionKey.interestOps(interests);
    }

    @Override
    public boolean mayRead() {
        return channel.isReadable();
    }

    @Override
    public boolean mayWrite() {
        return channel.isWritable();
    }

    @Override
    public boolean mayConnect() {
        return channel.isConnectionPending();
    }

    private SocketChannel createChannel() throws IOException {
        SocketChannel channel = SocketChannel.open();
        channel.configureBlocking(false);
        channel.socket().setSoTimeout(10000);
        channel.socket().setTcpNoDelay(true);
        return channel;
    }
