    private final long myCleanupTimeoutMillis;
    private final CleanupProcessor myCleanupProcessor;
    private final NewConnectionAllower myNewConnectionAllower;

    public ConnectionWorker(Selector selector, BlockingQueue<ConnectTask> connectQueue, BlockingQueue<WriteTask> writeQueue, 
                            Semaphore semaphore, List<KeyProcessor> keyProcessors, TimeService timeService, 
                            int selectorTimeout, long cleanupTimeout, CleanupProcessor cleanupProcessor, NewConnectionAllower newConnectionAllower) {
        this.selector = selector;
        this.myConnectQueue = connectQueue;
        this.myWriteQueue = writeQueue;
        this.mySemaphore = semaphore;
        this.myKeyProcessors = keyProcessors;
        this.myTimeService = timeService;
        this.mySelectorTimeoutMillis = selectorTimeout;
        this.myCleanupTimeoutMillis = cleanupTimeout;
        this.myCleanupProcessor = cleanupProcessor;
        this.myNewConnectionAllower = newConnectionAllower;
        lastCleanupTime = myTimeService.currentTimeMillis();
    }

    @Override
    public void run() {
        try {
            while (!stop) {
                mySemaphore.acquire();
                try {
                    connectToPeersFromQueue();
                    processSelectedKeys();
                } finally {
                    mySemaphore.release();
                }
                if (needRunCleanup()) {
                    cleanup();
                }
            }
        } catch (Exception e) {
            logger.error("Error in ConnectionWorker", e);
        } finally {
            try {
                selector.close();
            } catch (IOException e) {
                logger.warn("Failed to close selector", e);
            }
        }
    }

    private void connectToPeersFromQueue() throws IOException {
        while (!myConnectQueue.isEmpty()) {
            ConnectTask task = myConnectQueue.poll();
            if (task == null) break;
            if (myNewConnectionAllower.allow()) {
                SocketChannel channel = SocketChannel.open();
                channel.configureBlocking(false);
                channel.connect(new InetSocketAddress(task.getHost(), task.getPort()));
                channel.register(selector, SelectionKey.OP_CONNECT, task);
                logger.info("Connecting to {}", task.getHost());
            } else {
                logger.warn("Connection to {} is disallowed", task.getHost());
            }
        }
    }

    private void processSelectedKeys() throws IOException {
        Iterator<SelectionKey> keys = selector.selectedKeys().iterator();
        while (keys.hasNext()) {
            SelectionKey key = keys.next();
            keys.remove();
            processSelectedKey(key);
        }
    }

    private void processSelectedKey(SelectionKey key) throws IOException {
        if (key.isConnectable()) {
            SocketChannel channel = (SocketChannel) key.channel();
            if (channel.isConnectionPending()) {
                channel.finishConnect();
                key.interestOps(SelectionKey.OP_WRITE);
                logger.info("Connected to {}", ((ConnectTask) key.attachment()).getHost());
            }
        } else if (key.isWritable()) {
            processWriteTasks();
        } else if (key.isReadable()) {
            // Handle read operations if needed
        }
    }

    private boolean offerConnect(ConnectTask task, int timeout, TimeUnit unit) {
        return myConnectQueue.offer(task, timeout, unit);
    }

    private <T> boolean addTaskToQueue(T task, int timeout, TimeUnit unit, BlockingQueue<T> queue) {
        return queue.offer(task, timeout, unit);
    }

    private void processWriteTasks() throws IOException {
        while (!myWriteQueue.isEmpty()) {
            WriteTask task = myWriteQueue.poll();
            if (task == null) break;
            SocketChannel channel = (SocketChannel) task.getChannel();
            ByteBuffer buffer = ByteBuffer.wrap(task.getData());
            while (buffer.hasRemaining()) {
                int result = channel.write(buffer);
                if (result == 0) break;
            }
            logger.debug("Wrote {} bytes to {}", task.getData().length, task.getChannel());
        }
    }

    private void cleanup() {
        long currentTime = myTimeService.currentTimeMillis();
        if (currentTime - lastCleanupTime > myCleanupTimeoutMillis) {
            myCleanupProcessor.process();
            lastCleanupTime = currentTime;
        }
    }

    private boolean needRunCleanup() {
        return myTimeService.currentTimeMillis() - lastCleanupTime > myCleanupTimeoutMillis;
    }
}

