    private static final Object PRESENT = new Object();
    private static final ConcurrentMap<Session, Object> peers = new ConcurrentHashMap<>();

    @OnOpen
    public void onOpen(Session peer) {
        peers.put(peer, PRESENT);
        LOGGER.info("New connection: " + peer.getId());
    }

    @OnClose
    public void onClose(Session peer) {
        peers.remove(peer);
        LOGGER.info("Connection closed: " + peer.getId());
    }

    public void broadcastFigure(Figure figure, Session session) {
        for (Session target : peers.keySet()) {
            if (!target.equals(session)) {
                try {
                    target.getAsyncRemote().sendText(figure.toString());
                } catch (IOException e) {
                    LOGGER.severe("Error broadcasting figure: " + e.getMessage());
                }
            }
        }
    }

    public void broadcastSnapshot(ByteBuffer data, Session session) {
        for (Session target : peers.keySet()) {
            if (!target.equals(session)) {
                try {
                    target.getAsyncRemote().sendBinary(data);
                } catch (IOException e) {
                    LOGGER.severe("Error broadcasting snapshot: " + e.getMessage());
                }
            }
        }
    }
}

