        HealthCheckRequest request = HealthCheckRequest.getDefaultInstance();
        AwaitableStreamObserver<HealthCheckResponse> responseObserver = new AwaitableStreamObserver<>();
        stub.check(request, responseObserver);
        return responseObserver.getSingle();
    }

    public void shutdown() {
        channel.shutdown();
    }
}

