                // Log or handle exception
            }
        }
        for (KVPair<Boolean, RespT> result : results) {
            if (result.getKey()) {
                return result;
            }
        }
        return new KVPair<>(false, null);
    }

    public <ReqT, RespT> void streamingCall(MethodDescriptor<ReqT, RespT> method, ReqT request, StreamObserver<RespT> responseObserver, int retry) {
        for (int i = 0; i < retry; i++) {
            try {
                getStub().startStreaming(method, request, responseObserver);
                return;
            } catch (Exception e) {
                if (i == retry - 1) {
                    throw new RuntimeException("Failed after " + retry + " retries", e);
                }
                Thread.sleep(100 * (i + 1));
            }
        }
        throw new RuntimeException("Failed after " + retry + " retries");
    }

    public void close() {
        closeChannel(channel);
        chs.values().forEach(ManagedChannel::shutdown);
        chs.clear();
    }

    protected void closeChannel(ManagedChannel channel) {
        if (channel != null) {
            channel.shutdownNow();
        }
    }
}

