import java.io.BufferedInputStream;
import java.io.EOFException;
import java.io.IOException;
import java.io.OutputStream;
import java.net.InetAddress;
import java.net.ServerSocket;
import java.net.Socket;
import java.net.SocketException;

public final class DNSConnection {

    public static final int PORT = 53;
    public static final int MAX_MSG_LEN = 0xFFFF;
    protected static ServerSocket listener;
    protected Socket socket;
    protected byte[] msgBytes;
    protected int msgLen;
    protected BufferedInputStream in;
    protected OutputStream out;
    protected final byte[] lenBuf = new byte[2];

    public DNSConnection() {}

    public static void listen() throws IOException {
        ServerSocket curListener;
        if ((curListener = listener) == null) {
            try {
                listener = new ServerSocket(PORT);
            } catch (SecurityException e) {
                throw new SocketException("SecurityException: listen()");
            }
        }
        curListener = null;
    }

    public static void stopListening() {
        ServerSocket curListener;
        if ((curListener = listener) != null) {
            listener = null;
            try {
                curListener.close();
            } catch (IOException e) {}
            curListener = null;
        }
    }

    public void openIncoming() throws IOException {
        ServerSocket curListener;
        if ((curListener = listener) != null) {
            try {
                Socket socket = curListener.accept();
                BufferedInputStream in = new BufferedInputStream(socket.getInputStream(), DNSMsgHeader.UDP_PACKET_LEN);
                this.out = socket.getOutputStream();
                this.in = in;
                this.msgBytes = null;
                this.socket = socket;
                return;
            } catch (SecurityException e) {}
        }
        throw new SocketException(curListener == null ? "Not listening" : "SecurityException: accept()");
    }

    public void open(InetAddress server) throws NullPointerException, IOException {
        server.hashCode();
        try {
            Socket socket = new Socket(server, PORT);
            BufferedInputStream in = new BufferedInputStream(socket.getInputStream(), DNSMsgHeader.UDP_PACKET_LEN);
            this.out = socket.getOutputStream();
            this.in = in;
            this.socket = socket;
        } catch (SecurityException e) {
            throw new SocketException("SecurityException: connect(" + server.getHostAddress() + ")");
        }
        this.msgBytes = null;
    }

    public void send(byte[] msgBytes) throws NullPointerException, IOException {
        int msgLen;
        if ((msgLen = msgBytes.length) >= MAX_MSG_LEN)
            msgLen = MAX_MSG_LEN;
        OutputStream out;
        if ((out = this.out) == null)
            throw new SocketException("Connection closed");
        byte[] lenBuf = this.lenBuf;
        lenBuf[0] = (byte)(msgLen >> 8);
        lenBuf[1] = (byte)msgLen;
        out.write(lenBuf, 0, 2);
        out.write(msgBytes, 0, msgLen);
        out.flush();
    }

    public byte[] receive(boolean wait) throws IOException {
        byte[] msgBytes;
        int msgLen, len;
        BufferedInputStream in;
        if ((in = this.in) == null)
            throw new SocketException("Connection closed");
        if ((msgLen = this.msgLen) <= 0)
            msgLen = 0;
        if ((msgBytes = this.msgBytes) == null) {
            do {
                if (!wait && in.available() <= 0)
                    return null;
                else if ((len = in.read()) < 0)
                    throw new EOFException();
                else if (msgLen <= 0)
                    this.msgLen = msgLen = len + 1;
                else break;
            } while (true);
            if ((msgLen = ((msgLen - 1) << 8) | len) <= 0)
                msgLen = 0;
            this.msgBytes = msgBytes = new byte[msgLen];
            msgLen = 0;
        }
        for (int avail; (len = msgBytes.length - (this.msgLen = msgLen)) > 0; msgLen += len)
            if (!wait && (avail = in.available()) < len && (len = avail) <= 0)
                return null;
            else if ((len = in.read(msgBytes, msgLen, len)) < 0)
                throw new EOFException();
        this.msgBytes = null;
        this.msgLen = 0;
        return msgBytes;
    }

    public void close() {
        Socket socket;
        if ((socket = this.socket) != null) {
            this.socket = null;
            this.in = null;
            this.out = null;
            this.msgBytes = null;
            try {
                socket.close();
            } catch (IOException e) {}
            socket = null;
        }
    }
}

