            } catch (SocketException e) {
                // assume "Socket closed"
            } catch (IOException e) {
                throw new IllegalStateException(e);
            } finally {
                close();
            }
        }

        public void close() {
            try {
                server.close();
            } catch (IOException e) {
                // ignore
            }
        }
    }

