        }

        public void shouldStop() {
            shouldStop = true;
            try {
                clientSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private interface ImapInteraction {}
    private static class CloseConnection implements ImapInteraction {}
