class PebbleIoThread {

    private boolean mIsTCP = false;
    private BluetoothAdapter mBtAdapter;
    private BluetoothSocket mBtSocket;
    private Socket mTCPSocket;
    private InputStream mInStream;
    private OutputStream mOutStream;
    private Context mContext;
    private GBDevice gbDevice;

    PebbleIoThread(Context context, GBDevice device, BluetoothAdapter btAdapter) {
        mContext = context;
        gbDevice = device;
        mBtAdapter = btAdapter;
    }

    protected boolean connect() {
        String deviceAddress = gbDevice.getAddress();
        try {
            int firstColon = deviceAddress.indexOf(":");
            if (firstColon == deviceAddress.lastIndexOf(":")) {
                mIsTCP = true;
                InetAddress serverAddr = InetAddress.getByName(deviceAddress.substring(0, firstColon));
                mTCPSocket = new Socket(serverAddr, Integer.parseInt(deviceAddress.substring(firstColon + 1)));
                mInStream = mTCPSocket.getInputStream();
                mOutStream = mTCPSocket.getOutputStream();
            } else {
                mIsTCP = false;
                BluetoothDevice btDevice = mBtAdapter.getRemoteDevice(deviceAddress);
                final UUID UuidSDP = UUID.fromString("00001101-0000-1000-8000-00805f9b34fb");
                mBtSocket = btDevice.createRfcommSocketToServiceRecord(UuidSDP);
                mBtSocket.connect();
                mInStream = mBtSocket.getInputStream();
                mOutStream = mBtSocket.getOutputStream();
            }
            return true;
        } catch (IOException e) {
            mInStream = null;
            mOutStream = null;
            mBtSocket = null;
            return false;
        }
    }

    private void write_real(byte[] bytes) {
        try {
            if (mIsTCP) {
                ByteBuffer buf = ByteBuffer.allocate(bytes.length + 8);
                buf.order(ByteOrder.BIG_ENDIAN);
                buf.putShort((short) 0xfeed);
                buf.putShort((short) 1);
                buf.putShort((short) bytes.length);
                buf.put(bytes);
                buf.putShort((short) 0xbeef);
                mOutStream.write(buf.array());
                mOutStream.flush();
            } else {
                mOutStream.write(bytes);
                mOutStream.flush();
            }
        } catch (IOException e) {
            // Error handling
        }
    }

    public void quit() {
        if (mBtSocket != null) {
            try {
                mBtSocket.close();
            } catch (IOException ignored) {
            }
            mBtSocket = null;
        }
        if (mTCPSocket != null) {
            try {
                mTCPSocket.close();
            } catch (IOException ignored) {
            }
            mTCPSocket = null;
        }
    }
}

import android.net.LocalSocket;
import android.net.LocalSocketAddress;
import android.util.Log;

import com.max2idea.android.limbo.main.Config;
import com.max2idea.android.limbo.main.LimboApplication;

import org.json.JSONObject;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.net.Socket;

public class QmpClient {

    private static final String TAG = "QmpClient";
    private static final String requestCommandMode = "{ \"execute\": \"qmp_capabilities\" }";
    private static boolean external = false;

    public static void setExternal(boolean value) {
        external = value;
    }

    public synchronized static String sendCommand(String command) {
        String response = null;
        Socket pingSocket = null;
        LocalSocket localSocket = null;
        PrintWriter out = null;
        BufferedReader in = null;

        try {
            if(external) {
                pingSocket = new Socket(Config.QMPServer, Config.QMPPort);
                pingSocket.setSoTimeout(5000);
                out = new PrintWriter(pingSocket.getOutputStream(), true);
                in = new BufferedReader(new InputStreamReader(pingSocket.getInputStream()));
            } else {
                localSocket = new LocalSocket();
                String localQMPSocketPath = LimboApplication.getLocalQMPSocketPath();
                LocalSocketAddress localSocketAddr = new LocalSocketAddress(localQMPSocketPath, LocalSocketAddress.Namespace.FILESYSTEM);
                localSocket.connect(localSocketAddr);
                localSocket.setSoTimeout(5000);
                out = new PrintWriter(localSocket.getOutputStream(), true);
                in = new BufferedReader(new InputStreamReader(localSocket.getInputStream()));
            }
            sendRequest(out, QmpClient.requestCommandMode);
            response = tryGetResponse(in);
            sendRequest(out, command);
            response = tryGetResponse(in);
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            if (out != null)
                out.close();
            try {
                if (in != null)
                    in.close();
                if (pingSocket != null)
                    pingSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if(Config.debugQmp)
            Log.d(TAG, "Response: " + response);
        return response;
    }

    private static String tryGetResponse(BufferedReader in) throws Exception {
        String response = null;
        int trial = 0;
        while((response = getResponse(in)).equals("") && trial < 3){
            Thread.sleep(1000);
            trial++;
        }
        return response;
    }

    private static void sendRequest(PrintWriter out, String request) {
        if(Config.debugQmp)
            Log.d(TAG, "QMP request" + request);
        out.println(request);
    }

    private static String getResponse(BufferedReader in) throws Exception {
        String line;
        StringBuilder stringBuilder = new StringBuilder("");
        try {
            do {
                line = in.readLine();
                if (line != null) {
                    if(Config.debugQmp)
                        Log.d(TAG, "QMP response: " + line);
                    JSONObject object = new JSONObject(line);
                    String returnStr = null;
                    String errStr = null;

                    try {
                        if(line.contains("return"))
                            returnStr = object.getString("return");
                    } catch (Exception ex) {
                        if(Config.debugQmp)
                            ex.printStackTrace();
                    }

                    if (returnStr != null) {
                        stringBuilder.append(line);
                        stringBuilder.append("\n");
                        break;
                    }

                    try {
                        if(line.contains("error"))
                            errStr = object.getString("error");
                    } catch (Exception ex) {
                        if(Config.debugQmp)
                            ex.printStackTrace();
                    }
                    stringBuilder.append(line);
                    stringBuilder.append("\n");
                    if (errStr != null) {
                        break;
                    }
                } else
                    break;
            } while (true);
        } catch (Exception ex) {
            Log.e(TAG, "Could not get Response: " + ex.getMessage());
            if(Config.debugQmp)
                ex.printStackTrace();
        }
        return stringBuilder.toString();
    }
}

