    private void processMetrics() {
        while (running.get()) {
            try {
                List<String> metrics = new ArrayList<>();
                while ((metrics.add(queue.poll())) && metrics.size() < 100) {
                    // Poll until queue is empty or 100 metrics are collected
                }
                if (!metrics.isEmpty()) {
                    sendMetrics(metrics);
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
            try {
                Thread.sleep(100);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                break;
            }
        }
    }

    private void sendMetrics(List<String> metrics) {
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < metrics.size(); i++) {
            sb.append(metrics.get(i));
            if (i < metrics.size() - 1) {
                sb.append("\n");
            }
        }
        byte[] data = sb.toString().getBytes(CHARSET);
        DatagramPacket packet = new DatagramPacket(data, data.length, serverAddress, serverPort);
        try {
            socket.send(packet);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

