        if (responseCode == HttpURLConnection.HTTP_OK) {
            try (BufferedReader in = new BufferedReader(new InputStreamReader(conn.getInputStream(), charSet))) {
                StringBuilder response = new StringBuilder();
                String line;
                while ((line = in.readLine()) != null) {
                    response.append(line);
                }
                return response.toString();
            }
        } else {
            logger.error("HTTP error code: " + responseCode);
            throw new IOException("HTTP error code: " + responseCode);
        }
    }

    public SimpleHttpResult httpRequest(SimpleHttpParam httpParam) throws IOException {
        URL url = new URL(httpParam.getUrl());
        HttpURLConnection conn = (HttpURLConnection) url.openConnection();
        conn.setRequestMethod(httpParam.getMethod());
        conn.setReadTimeout(httpParam.getReadTimeout());
        conn.setConnectTimeout(httpParam.getConnectTimeout());

        if (httpParam.getHeaders() != null) {
            for (Map.Entry<String, String> entry : httpParam.getHeaders().entrySet()) {
                conn.setRequestProperty(entry.getKey(), entry.getValue());
            }
        }

        if (httpParam.getParams() != null) {
            String paramString = mapToQueryString(httpParam.getParams(), httpParam.getCharset());
            if ("POST".equals(httpParam.getMethod())) {
                conn.setDoOutput(true);
                try (OutputStream os = conn.getOutputStream()) {
                    byte[] input = paramString.getBytes(httpParam.getCharset());
                    os.write(input, 0, input.length);
                }
            } else {
                String fullUrl = url.toString() + (url.toString().contains("?") ? "&" : "?") + paramString;
                conn.setURL(new URL(fullUrl));
            }
        }

        int responseCode = conn.getResponseCode();
        if (responseCode == HttpURLConnection.HTTP_OK) {
            try (BufferedReader in = new BufferedReader(new InputStreamReader(conn.getInputStream(), httpParam.getCharset()))) {
                StringBuilder response = new StringBuilder();
                String line;
                while ((line = in.readLine()) != null) {
                    response.append(line);
                }
                return new SimpleHttpResult(response.toString(), responseCode);
            }
        } else {
            logger.error("HTTP error code: " + responseCode);
            throw new IOException("HTTP error code: " + responseCode);
        }
    }

    private String mapToQueryString(Map<String, String> parameters, String charSet) {
        if (parameters == null || parameters.isEmpty()) return "";
        StringBuilder sb = new StringBuilder();
        for (Map.Entry<String, String> entry : parameters.entrySet()) {
            sb.append(urlEncode(entry.getKey(), charSet));
            sb.append("=");
            sb.append(urlEncode(entry.getValue(), charSet));
            sb.append("&");
        }
        return sb.substring(0, sb.length() - 1);
    }

    private String urlEncode(String url, String charSet) {
        try {
            return URLEncoder.encode(url, charSet);
        } catch (UnsupportedEncodingException e) {
            logger.error("URL encoding error", e);
            throw new RuntimeException("URL encoding error", e);
        }
    }

    private List<String> makeStringList(Object value) {
        if (value instanceof Collection) {
            return new ArrayList<>((Collection<? extends String>) value);
        } else if (value instanceof String) {
            return Arrays.asList((String) value);
        } else {
            return new ArrayList<>();
        }
    }

    public void setSSLSocketFactory(HttpURLConnection urlConn, boolean sslVerify, boolean hostnameVerify, TrustKeyStore trustKeyStore, ClientKeyStore clientKeyStore) {
        if (sslVerify) {
            TrustManager[] tmf = trustKeyStore.getTrustManagers();
            sslVerify = true;
        } else {
            tmf = trustAnyManagers;
        }

        try {
            SSLContext sslContext = SSLContext.getInstance("TLS");
            sslContext.init(tmf, null, new SecureRandom());
            urlConn.setSSLSocketFactory(sslContext.getSocketFactory());
        } catch (Exception e) {
            logger.error("SSL configuration error", e);
        }

        if (hostnameVerify) {
            urlConn.setHostnameVerifier(new TrustAnyHostnameVerifier());
        }
    }
