import org.apache.kafka.clients.admin.AdminClient;
import org.apache.kafka.clients.admin.AdminClientConfig;
import org.apache.kafka.clients.admin.CreateTopicsResult;
import org.apache.kafka.clients.admin.NewTopic;
import org.apache.kafka.common.KafkaFuture;
import org.springframework.boot.autoconfigure.kafka.KafkaProperties;
import java.util.Collections;
import java.util.Map;
import java.util.concurrent.TimeUnit;

public class KafkaTopicCreator {

    private final Map<String, Object> adminClientProperties;
    private final int operationTimeout = 30;

    public KafkaTopicCreator(KafkaProperties kafkaProperties) {
        this.adminClientProperties = kafkaProperties.buildAdminProperties(null);
    }

    public void createTopic(String topicName, int partitions, short replicationFactor) throws Exception {
        try (AdminClient adminClient = AdminClient.create(this.adminClientProperties)) {
            NewTopic newTopic = new NewTopic(topicName, partitions, replicationFactor);
            CreateTopicsResult createTopicsResult = adminClient.createTopics(Collections.singletonList(newTopic));
            KafkaFuture<Void> future = createTopicsResult.all();
            future.get(this.operationTimeout, TimeUnit.SECONDS);
        }
    }

    public Map<String, Object> getAdminClientProperties() {
        return Collections.unmodifiableMap(this.adminClientProperties);
    }
}

