import java.io.ByteArrayOutputStream;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.Map;

public final class HttpURLConnectionUtils {

    private static final String TAG = HttpURLConnectionUtils.class.getSimpleName();
    private static final int TIMEOUT_IN_MILLIONS = 5000;
    public static final String BAIDU_URL = "https://www.baidu.com";

    public interface Callback {
        void onResponse(String result, long response);
        void onFail(Throwable error);
    }

    public interface TimeCallback {
        void onResponse(long millis);
        void onFail(Throwable error);
    }

    public static void doGetAsync(final String urlStr, final Callback callback) {
        new Thread(() -> {
            try {
                request("GET", urlStr, null, null, callback);
            } catch (Exception e) {
                e.printStackTrace();
            }
        }).start();
    }

    public static void doPostAsync(final String urlStr, final String params, final Callback callback) {
        new Thread(() -> {
            try {
                request("POST", urlStr, null, params, callback);
            } catch (Exception e) {
                e.printStackTrace();
            }
        }).start();
    }

    public static void getNetTime(final TimeCallback callback) {
        getNetTime(BAIDU_URL, callback);
    }

    public static void getNetTime(final String urlStr, final TimeCallback callback) {
        new Thread(() -> reqNetTime(urlStr, callback)).start();
    }

    private static void request(
            final String method,
            final String urlStr,
            final Map<String, String> headers,
            final String params,
            final Callback callback
    ) {
        HttpURLConnection connection = null;
        InputStream is = null;
        ByteArrayOutputStream baos = null;
        try {
            URL url = new URL(urlStr);
            connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod(method);

            if (headers != null) {
                for (Map.Entry<String, String> entry : headers.entrySet()) {
                    connection.setRequestProperty(entry.getKey(), entry.getValue());
                }
            }

            if (params != null && params.length() != 0) {
                connection.setDoInput(true);
                connection.setDoOutput(true);
                connection.setUseCaches(false);
                OutputStream os = connection.getOutputStream();
                os.write(params.getBytes());
                os.flush();
                os.close();
            }

            connection.setConnectTimeout(TIMEOUT_IN_MILLIONS);
            connection.setReadTimeout(TIMEOUT_IN_MILLIONS);

            int responseCode = connection.getResponseCode();
            if (responseCode >= 200 && responseCode < 300) {
                is = connection.getInputStream();
                baos = new ByteArrayOutputStream();
                byte[] buffer = new byte[1024];
                int len;
                while (((len = is.read(buffer)) != -1)) {
                    baos.write(buffer, 0, len);
                }
                String result = baos.toString();
                if (callback != null) {
                    callback.onResponse(result, connection.getDate());
                }
            } else {
                if (callback != null) {
                    callback.onFail(new Exception("responseCode not >= 200 or < 300, code: " + responseCode));
                }
            }
        } catch (Exception e) {
            if (callback != null) {
                callback.onFail(e);
            }
        } finally {
            try {
                if (baos != null) baos.close();
                if (is != null) is.close();
            } catch (Exception ignore) {}

            if (connection != null) {
                try {
                    connection.disconnect();
                } catch (Exception ignore) {}
            }
        }
    }

    private static void reqNetTime(final String urlStr, final TimeCallback callback) {
        HttpURLConnection connection = null;
        try {
            URL url = new URL(urlStr);
            connection = (HttpURLConnection) url.openConnection();
            long date = connection.getDate();
            if (date <= 0) {
                date = -1L;
            }
            if (callback != null) {
                callback.onResponse(date);
            }
        } catch (Exception e) {
            if (callback != null) {
                callback.onFail(e);
            }
        } finally {
            if (connection != null) {
                try {
                    connection.disconnect();
                } catch (Exception ignore) {}
            }
        }
    }
}

