        } else {
            this.resporityServerUrl = resporityServerUrl;
        }
    }

    private KnowledgePackage parseResponse(String response) {
        try {
            JSONObject json = new JSONObject(response);
            KnowledgePackage kp = new KnowledgePackage();
            kp.setId(json.getString("id"));
            kp.setTimestamp(json.getString("timestamp"));
            kp.setContent(json.getString("content"));
