import java.io.IOException;
import java.lang.foreign.Arena;
import java.lang.foreign.MemorySegment;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class MemorySegmentReaderSupplier implements ReaderSupplier {
    private final Arena arena;
    private final MemorySegment memory;

    public MemorySegmentReaderSupplier(Path path) throws IOException {
        this.arena = Arena.ofShared();
        try (var ch = FileChannel.open(path, StandardOpenOption.READ)) {
            this.memory = ch.map(FileChannel.MapMode.READ_ONLY, 0L, ch.size(), arena);
        } catch (Throwable e) {
            arena.close();
            if (e instanceof IOException) {
                throw (IOException) e;
            }
            throw new RuntimeException(e);
        }
    }

    @Override
    public MemorySegmentReader get() {
        return new MemorySegmentReader(memory);
    }

    @Override
    public void close() {
        arena.close();
    }
}

