import java.io.IOException;
import java.nio.file.Path;
import java.nio.file.FileChannel;
import java.nio.file.OpenOption;
import java.nio.file.StandardOpenOption;
import jdk.incubator.foreign.Arena;
import jdk.incubator.foreign.MemorySegment;
import jdk.incubator.foreign.MemorySegmentReader;

public class MemorySegmentReaderSupplier implements ReaderSupplier<MemorySegmentReader> {
    private final Arena arena;
    private final MemorySegment memory;

    public MemorySegmentReaderSupplier(Path path) throws IOException {
        try (FileChannel fileChannel = FileChannel.open(path, StandardOpenOption.READ)) {
            long fileSize = fileChannel.size();
            arena = Arena.allocateNative(sizeOf(fileSize));
            memory = fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, fileSize).toMemorySegment(arena);
        }
    }

    @Override
    public MemorySegmentReader get() {
        return new MemorySegmentReader(memory);
    }

    @Override
    public void close() {
        if (arena != null) {
            arena.close();
        }
    }

    private static long sizeOf(long fileSize) {
        return fileSize;
    }
}

