        if (bout != null) {
            try {
                bout.close();
            } catch (Exception e) {
                SALog.printStackTrace(e);
            }
        }
        if (connection != null) {
            try {
                connection.disconnect();
            } catch (Exception e) {
                SALog.printStackTrace(e);
            }
        }
    }
}

