import java.io.*;
import java.net.*;
import javax.net.ssl.*;

public class HttpDebugIdSender {
    private String distinctId;
    private String infoId;
    private String serverUrl;

    public HttpDebugIdSender(String serverUrl, String distinctId, String infoId) {
        this.serverUrl = serverUrl;
        this.distinctId = distinctId;
        this.infoId = infoId;
    }

    public void send() {
        try {
            String finalUrl = serverUrl + "/" + infoId + "?distinct_id=" + distinctId;
            sendHttpRequest(finalUrl, true);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private void sendHttpRequest(String serverUrl, boolean isRedirects) throws IOException {
        URL url = new URL(serverUrl);
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setRequestMethod("POST");
        connection.setDoOutput(true);
        connection.setInstanceFollowRedirects(isRedirects);

        try (OutputStream os = connection.getOutputStream()) {
            String jsonPayload = "{\"distinct_id\":\"" + distinctId + "\"}";
            byte[] input = jsonPayload.getBytes("utf-8");
            os.write(input, 0, input.length);
        }

        int responseCode = connection.getResponseCode();
        if (responseCode != 200) {
            throw new IOException("Server returned HTTP response code: " + responseCode);
        }
    }

    private void closeStream(ByteArrayOutputStream out, OutputStream out2, BufferedOutputStream bout, HttpURLConnection connection) {
        try {
            if (out != null) out.close();
            if (out2 != null) out2.close();
            if (bout != null) bout.close();
            if (connection != null) connection.disconnect();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

