import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.util.concurrent.atomic.AtomicLong;

record Block(long offset, int length, FileChannel channel) {
    public Iterable<OsmElement> decodeElements() throws IOException {
        return new OsmElementIterable(this);
    }
}

class LazyBlock {
    private final long offset;
    private final int length;
    private final FileChannel channel;
    private MappedByteBuffer buffer;
    private boolean decoded;

    public LazyBlock(long offset, int length, FileChannel channel) {
        this.offset = offset;
        this.length = length;
        this.channel = channel;
    }

    public Iterable<OsmElement> decodeElements() throws IOException {
        if (buffer == null || decoded) {
            buffer = MappedByteBuffer.map(channel, offset, length);
            decoded = true;
        }
        return new OsmElementIterable(this);
    }

    public void close() throws IOException {
        if (buffer != null) {
            buffer.close();
        }
    }
}

class OsmInputFile {
    private final Path path;
    private final boolean lazy;
    private FileChannel channel;

    public OsmInputFile(Path path, boolean lazy) {
        this.path = path;
        this.lazy = lazy;
    }

    public FileChannel openChannel() throws IOException {
        if (channel == null || channel.isOpen() && !channel.isValid()) {
            channel = FileChannel.open(path, java.nio.file.StandardOpenOption.READ);
        }
        return channel;
    }

    public int readInt(FileChannel channel) throws IOException {
        return channel.readInt();
    }

    public byte[] readBytes(FileChannel channel, int length) throws IOException {
        byte[] bytes = new byte[length];
        channel.read(bytes);
        return bytes;
    }

    public byte[] readBytes(FileChannel channel, long offset, int length) throws IOException {
        byte[] bytes = new byte[length];
        channel.position(offset);
        channel.read(bytes);
        return bytes;
    }

    public BlobHeader readBlobHeader(FileChannel channel) throws IOException {
        int size = readInt(channel);
        int type = readInt(channel);
        int version = readInt(channel);
        int flags = readInt(channel);
        return new BlobHeader(size, type, version, flags);
    }

    class LazyReader implements OsmBlockSource {
        private final AtomicLong position = new AtomicLong(0);

        public void forEachBlock(Consumer<Block> consumer) throws IOException {
            while (position.get() < getBlockSize()) {
                long offset = position.get();
                int length = getBlockLength(offset);
                consumer.accept(new LazyBlock(offset, length, openChannel()));
                position.addAndGet(length);
            }
        }

        public void close() throws IOException {
            if (channel != null && channel.isOpen()) {
                channel.close();
            }
        }
    }

    public OsmBlockSource getLazyReader() throws IOException {
        return new LazyReader();
    }
}

