            lastWriteFuture = channel.writeAndFlush("PING\r\n");
            Scanner scanner = new Scanner(System.in);
            while (true) {
                System.out.print("Enter Redis command: ");
                String line = scanner.nextLine();
                if (line.equalsIgnoreCase("exit")) {
                    break;
                }
                channel.writeAndFlush(line + "\r\n");
            }
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            stop();
        }
    }

    public void stop() {
        if (channel != null) {
            channel.close();
        }
        if (group != null) {
            group.shutdownGracefully();
        }
    }

    private void initChannel(SocketChannel ch) {
        ch.pipeline().addLast(new RedisDecoder());
        ch.pipeline().addLast(new RedisEncoder());
        ch.pipeline().addLast(new RedisClientHandler());
    }

