import java.io.DataOutputStream;
import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.util.logging.Logger;

public abstract class AppendStoreMmap {
    protected DataOutputStream outputStream;
    protected int segmentBits;
    protected long segmentMask;
    protected long segmentBytes;
    protected Path path;
    protected boolean madvise;
    protected long outIdx;
    protected MappedByteBuffer[] segments;
    protected FileChannel channel;
    protected Logger LOGGER;

    public AppendStoreMmap(Path path, boolean madvise) {
        this(path, 1 << 30, madvise); // Default segment size is 1GB
    }

    public AppendStoreMmap(Path path, long segmentSizeBytes, boolean madvise) {
        this.path = path;
        this.madvise = madvise;
        this.segmentBytes = segmentSizeBytes;
        this.segmentBits = (int) Math.log(segmentSizeBytes) / Math.log(2);
        this.segmentMask = (1L << segmentBits) - 1;
        try {
            channel = FileChannel.open(path, java.nio.file.StandardOpenOption.CREATE, java.nio.file.StandardOpenOption.WRITE);
            initializeSegments();
        } catch (IOException e) {
            LOGGER.severe("Error initializing file channel: " + e.getMessage());
        }
    }

    protected void initializeSegments() throws IOException {
        long fileSize = channel.size();
        long numSegments = (fileSize + segmentBytes - 1) / segmentBytes;
        segments = new MappedByteBuffer[(int) numSegments];
        for (int i = 0; i < segments.length; i++) {
            long segmentStart = i * segmentBytes;
            segments[i] = channel.map(FileChannel.MapMode.READ_WRITE, segmentStart, segmentBytes);
            if (madvise) {
                channel.madvise(MappedByteBuffer.class.cast(segments[i]));
            }
        }
        outIdx = 0;
    }

    public MappedByteBuffer[] getSegments() {
        if (segments == null || segments.length == 0) {
            try {
                initializeSegments();
            } catch (IOException e) {
                LOGGER.severe("Error initializing segments: " + e.getMessage());
            }
        }
        return segments;
    }

    public void close() {
        try {
            if (outputStream != null) {
                outputStream.close();
            }
            if (channel != null) {
                channel.close();
            }
        } catch (IOException e) {
            LOGGER.severe("Error closing resources: " + e.getMessage());
        }
    }

    public long diskUsageBytes() {
        try {
            return channel.size();
        } catch (IOException e) {
            LOGGER.severe("Error getting disk usage: " + e.getMessage());
            return -1;
        }
    }
}
