            if (command != null) {
                executeCommand(ctx, command);
            }
        }

        public void onBinaryMessage(ChannelHandlerContext ctx, byte[] data) {
            logger.info("Received binary message: {} bytes", data.length);
            // Process binary data
            // For simplicity, we'll just log the length here
        }

        public void initLocalClient(ChannelHandlerContext ctx, Command command) {
            logger.info("Initializing new client protocol for command: {}", command);
            Protocol protocol = new Protocol(ctx, command);
            protocolList.add(protocol);
        }

        public void executeCommand(ChannelHandlerContext ctx, Command command) {
            logger.info("Executing command: {}", command);
            for (Protocol protocol : protocolList) {
                if (protocol.matches(command)) {
                    protocol.execute(command);
                    break;
                }
            }
        }

        private Command parseCommand(String text) {
            // Simple command parsing logic
            if (text.startsWith("CMD_")) {
                return new Command(text.substring(4));
            }
            return null;
        }
    }
}

