import io.netty.handler.codec.http.HttpResponse;
import io.netty.handler.codec.http.FullHttpResponse;
import io.netty.handler.codec.http.HttpVersion;
import io.netty.handler.codec.http.QueryStringDecoder;
import io.netty.handler.codec.http.HttpHeaders;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class EmbedHttpServer {
    private final int port;
    private final DefaultController defaultController;
    private final List<Object> controllerList;
    private final Logger LOGGER;

    public EmbedHttpServer(int port) {
        this.port = port;
        this.defaultController = new DefaultController();
        this.controllerList = new ArrayList<>();
        this.LOGGER = LoggerFactory.getLogger(EmbedHttpServer.class);
    }

    public FullHttpResponse invoke(String uri, Channel channel, FullHttpRequest request) {
        String normalizedUri = fixUri(uri);
        for (Object controller : controllerList) {
            if (controller instanceof Controller) {
                Method method = ((Controller) controller).getMethod(normalizedUri);
                if (method != null) {
                    try {
                        Object result = method.invoke(controller);
                        FullHttpResponse response = new FullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK);
                        response.setContent(result.toString().getBytes());
                        return response;
                    } catch (Exception e) {
                        LOGGER.error("Error invoking controller method", e);
                        return new FullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.INTERNAL_SERVER_ERROR);
                    }
                }
            }
        }
        return defaultController.handleRequest(normalizedUri, request, channel);
    }

    private String fixUri(String uri) {
        if (uri == null || uri.isEmpty()) {
            return "/";
        }
        String normalized = uri.trim();
        if (normalized.startsWith("/")) {
            return normalized;
        }
        return "/" + normalized;
    }

    public void start(GenericFutureListener<ChannelFuture> startedListener) {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap bootstrap = new ServerBootstrap();
            bootstrap.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .option(ChannelOption.SO_BACKLOG, 100)
                    .handler(new LoggingHandler(LogLevel.INFO))
                    .childHandler(new ChannelInitializer<>() {
                        @Override
                        protected void initChannel(Channel ch) throws Exception {
                            ch.pipeline().addLast(new HttpServerHandler());
                        }
                    })
                    .childOption(ChannelOption.SO_REUSEADDR, true)
                    .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture future = bootstrap.bind(port).addListener(startedListener);
            future.sync();
        } catch (Exception e) {
            LOGGER.error("Error starting server", e);
        } finally {
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
        }
    }

    public EmbedHttpServer addController(Object obj) {
        controllerList.add(obj);
        return this;
    }

    static class Controller {
        private final Map<String, Method> methods = new HashMap<>();

        public Method getMethod(String uri) {
            return methods.get(uri);
        }

        public void setMethod(String uri, Method method) {
            methods.put(uri, method);
        }
    }

    static class DefaultController {
        public FullHttpResponse handleRequest(String uri, FullHttpRequest request, Channel channel) {
            FullHttpResponse response = new FullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.NOT_FOUND);
            response.setContent("404 Not Found".getBytes());
            return response;
        }
    }

    static class HttpServerHandler extends io.netty.handler.codec.http.HttpServerHandler {
        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) throws Exception {
            if (msg instanceof FullHttpRequest) {
                FullHttpRequest request = (FullHttpRequest) msg;
                String uri = request.uri();
                FullHttpResponse response = new EmbedHttpServer().invoke(uri, ctx.channel(), request);
                ctx.write(response);
                ctx.flush();
            }
            super.channelRead(ctx, msg);
        }
    }
}

