import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.TrustManagerFactory;
import java.io.File;
import java.io.IOException;
import java.net.InetSocketAddress;
import java.security.KeyStore;
import java.util.Properties;

public class EchoServer {
    private final boolean ssl;
    private final int port;

    public EchoServer() {
        this.ssl = Boolean.getBoolean("ssl");
        this.port = Integer.getInteger("port", 8007);
    }

    public void start() throws Exception {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();

        try {
            ServerBootstrap bootstrap = new ServerBootstrap();
            bootstrap.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .option(ChannelOption.SO_BACKLOG, 100)
                    .childOption(ChannelOption.SO_REUSEADDR, true)
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        protected void initChannel(SocketChannel ch) throws Exception {
                            ch.pipeline().addLast(new StringDecoder());
                            ch.pipeline().addLast(new StringEncoder());
                            ch.pipeline().addLast(new EchoServerHandler());
                        }
                    });

            if (ssl) {
                SslContext sslCtx = createSslContext();
                bootstrap.ssl(sslCtx);
            }

            ChannelFuture future = bootstrap.bind(port).sync();
            future.channel().closeFuture().sync();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    private SslContext createSslContext() throws IOException {
        Properties props = new Properties();
        props.load(new File("ssl.properties").openStream());

        String keyStorePath = props.getProperty("keyStorePath");
        String keyStorePassword = props.getProperty("keyStorePassword");
        String keyAlias = props.getProperty("keyAlias");

        KeyStore keyStore = KeyStore.getInstance(KeyStore.getDefaultType());
        keyStore.load(new File(keyStorePath).openStream(), keyStorePassword.toCharArray());

        TrustManagerFactory tmf = TrustManagerFactory.getInstance(TrustManagerFactory.getDefaultAlgorithm());
        tmf.init(keyStore);

        return new SslContextBuilder()
                .trustManager(tmf)
                .keyManager(keyStore, keyAlias, keyStorePassword.toCharArray())
                .build();
    }
}

