    }

    private static class HttpServerHandler extends io.netty.handler.codec.http.HttpServerHandler {
        private final ApplicationContext ctx;

        public HttpServerHandler(ApplicationContext ctx) {
            this.ctx = ctx;
        }

        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) throws Exception {
            if (msg instanceof io.netty.handler.codec.http.FullHttpRequest request) {
                String response = "Hello from Netty TCP server!";
                io.netty.handler.codec.http.FullHttpResponse responseMsg = new DefaultFullHttpResponse(
                        HttpVersion.HTTP_1_1, HttpResponseStatus.OK, Unpooled.copiedBuffer(response, CharsetUtil.UTF_8));
