                responseMsg.headers().set(HttpHeaderNames.CONTENT_LENGTH, responseMsg.content().readableBytes());
                ctx.write(responseMsg);
                ctx.writeAndFlush(LastHttpWriteStatus.SUCCESSFUL);
            }
            super.channelRead(ctx, msg);
        }
