    private final Set<Connection> connections = new ConcurrentHashMap<>();
    private final Consumer<Connection> onConnect;
    private final EventLoopGroup eventLoopGroup;
    private final ServerBootstrap server;

    public NettyServer(String host, int port, Consumer<Connection> onConnect) {
        this.host = host;
        this.port = port;
        this.onConnect = onConnect;
        this.eventLoopGroup = new NioEventLoopGroup();
        this.server = new ServerBootstrap();
    }

    public void start() {
        server.group(eventLoopGroup)
              .channel(NioServerSocketChannel.class)
              .bind(new InetSocketAddress(host, port))
              .addListener(future -> {
