            server.close().awaitUninterruptibly();
        }
        if (eventLoopGroup != null) {
            eventLoopGroup.shutdownGracefully();
        }
    }

    public static class Connection {
        private final SocketChannel channel;
        private final AtomicBoolean active = new AtomicBoolean(true);

        public Connection(SocketChannel channel) {
            this.channel = channel;
        }

        public boolean isActive() {
            return active.get();
        }

        public void close() {
            if (active.compareAndSet(true, false)) {
                channel.close();
            }
        }
    }

    public static class ConnectionHandler extends SimpleChannelInboundHandler<String> {
        private final Set<Connection> connections;
        private final Consumer<Connection> onConnect;

        public ConnectionHandler(Set<Connection> connections, Consumer<Connection> onConnect) {
            this.connections = connections;
            this.onConnect = onConnect;
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) {
            Connection connection = new Connection((SocketChannel) ctx.channel());
            connections.add(connection);
            onConnect.accept(connection);
        }

        @Override
        public void channelInactive(ChannelHandlerContext ctx) {
            Connection connection = (Connection) ctx.channel().attr(Connection.ATTR).get();
            if (connection != null) {
                connections.remove(connection);
            }
        }

        @Override
        protected void channelRead0(ChannelHandlerContext ctx, String msg) {
            // Handle incoming message
            System.out.println("Received: " + msg);
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            cause.printStackTrace();
            ctx.close();
        }
    }

    public static final AttributeKey<Connection> ATTR = AttributeKey.newInstance("connection");
}

