        public String execute() {
            return strings[new java.util.Random().nextInt(strings.length)];
        }
    }

    public static class DeviceCommand extends Command {
        public DeviceCommand(String command) {
            super(command);
        }
        @Override
        public String execute() {
            return BaseServer.getDevicesJSON();
        }
    }
