import io.netty.handler.codec.serialization.ObjectDecoder;
import io.netty.handler.codec.serialization.ObjectEncoder;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslHandler;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;

import java.io.Serializable;
import java.util.concurrent.atomic.AtomicBoolean;

public class ObjectEchoServer {
    private final boolean ssl;
    private final int port;
    private SslContext sslCtx;
    private final AtomicBoolean running = new AtomicBoolean(false);

    public ObjectEchoServer(boolean ssl, int port) {
        this.ssl = ssl;
        this.port = port;
    }

    public void start() throws Exception {
        if (running.get()) {
            throw new IllegalStateException("Server is already running");
        }

        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();

        try {
            if (ssl) {
                sslCtx = SslContext.newServerContext();
            }

            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .handler(new LoggingHandler(LogLevel.INFO))
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) throws Exception {
                     ChannelPipeline pipeline = ch.pipeline();
                     if (ssl) {
                         pipeline.addLast(sslCtx.newHandler(ch.channel()));
                     }
                     pipeline.addLast(new ObjectDecoder(Object.class));
                     pipeline.addLast(new ObjectEncoder());
                     pipeline.addLast(new ObjectEchoHandler());
                 }
             })
             .option(ChannelOption.SO_REUSEADDR, true)
             .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture future = b.bind(port).sync();

            running.set(true);

            future.channel().closeFuture().sync();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
            if (ssl) {
                sslCtx.close();
            }
        }
    }

    public void stop() {
        running.set(false);
    }

    private static class ObjectEchoHandler extends io.netty.handler.codec.ByteToMessageDecoder {
        @Override
        protected void decode(io.netty.buffer.ByteBuf in, java.util.List<Object> out) throws Exception {
            if (in.readableBytes() >= 4) {
                int length = in.readInt();
                if (in.readableBytes() >= length) {
                    byte[] data = new byte[length];
                    in.readBytes(data);
                    out.add(java.util.Arrays.copyOf(data, length));
                }
            }
        }
    }
}

