    private AtomicInteger rejectCounter;
    private NettyServerChannelManage channelManage;

    public NettyServer(URL url, MessageHandler messageHandler) {
        this.messageHandler = messageHandler;
        this.standardThreadExecutor = Executors.newFixedThreadPool(url.getWorkerThreads());
        this.rejectCounter = new AtomicInteger(0);
        this.channelManage = new NettyServerChannelManage();
        this.bossGroup = new NioEventLoopGroup(url.getBossThreads());
