        this.LOGGER = LoggerFactory.getLogger(NettyRemotingServer.class);
        this.serverBootstrap = new ServerBootstrap();
        this.bossSelectorGroup = new NioEventLoopGroup(config.getBossThreadCount());
        this.workerSelectorGroup = new NioEventLoopGroup(config.getWorkerThreadCount());
