    public HttpTestServer(int port, boolean fail, BiConsumer<HttpRequest, JsonGenerator> requestHandler, @Nullable Map<String, String> headers) {
        this.port = port;
        this.fail = fail;
        this.requestHandler = requestHandler;
        this.headers = headers;
