import io.netty.handler.codec.MessageToByteEncoder;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;

public class ProtocolServer {
    private final int port;
    public static final int MAX_FRAME_LENGTH = 1024 * 1024; // 1MB
    public static final int LENGTH_FIELD_LENGTH = 4;
    public static final int LENGTH_FIELD_OFFSET = 6;
    public static final int LENGTH_ADJUSTMENT = 0;
    public static final int INITIAL_BYTES_TO_STRIP = 0;

    public ProtocolServer(int port) {
        this.port = port;
    }

    public void run() throws Exception {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();

        try {
            ServerBootstrap bootstrap = new ServerBootstrap();
            bootstrap.group(bossGroup, workerGroup)
                     .channel(NioServerSocketChannel.class)
                     .handler(new LoggingHandler(LogLevel.INFO))
                     .childHandler(new ChannelInitializer<SocketChannel>() {
                         @Override
                         protected void initChannel(SocketChannel ch) {
                             ChannelPipeline pipeline = ch.pipeline();
                             pipeline.addLast(new MessageToByteEncoder<CustomMessage>() {
                                 @Override
                                 protected int encodeLength(CustomMessage msg) {
                                     return LENGTH_FIELD_LENGTH + msg.getBody().length;
                                 }

                                 @Override
                                 protected void encode(ChannelHandlerContext ctx, CustomMessage msg, ByteBuf out) {
                                     out.writeShort(LENGTH_FIELD_LENGTH);
                                     out.writeBytes(msg.getBody());
                                 }
                             });
                             pipeline.addLast(new ByteToMessageDecoder() {
                                 @Override
                                 protected boolean decode(ChannelHandlerContext ctx, ByteBuf in, List<Object> out) throws Exception {
                                     if (in.readableBytes() < LENGTH_FIELD_LENGTH) {
                                         return false;
                                     }
                                     int length = in.readInt();
                                     if (in.readableBytes() < length) {
                                         return false;
                                     }
                                     byte[] data = new byte[length];
                                     in.readBytes(data);
                                     out.add(new CustomMessage(data));
                                     return true;
                                 }
                             });
                             pipeline.addLast(new CustomMessageHandler());
                         }
                     })
                     .option(ChannelOption.SO_REUSEADDR, true)
                     .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture future = bootstrap.bind(port).sync();
            future.channel().closeFuture().sync();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    public static class CustomMessage {
        private byte[] body;

        public CustomMessage(byte[] body) {
            this.body = body;
        }

        public byte[] getBody() {
            return body;
        }
    }

    public static class CustomMessageHandler extends SimpleChannelInboundHandler<CustomMessage> {
        @Override
        protected void channelRead0(ChannelHandlerContext ctx, CustomMessage msg) {
            System.out.println("Received message: " + new String(msg.getBody()));
