public class NettyHttpServer {
    private final EventLoopGroup bossGroup = new NioEventLoopGroup();
    private final EventLoopGroup workerGroup = new NioEventLoopGroup();
    private int port;
    private ChannelFuture channelFuture;

    public int startServer() {
        ServerBootstrap bootstrap = new ServerBootstrap();
        bootstrap.group(bossGroup, workerGroup)
                .channel(NioServerSocketChannel.class)
                .handler(new LoggingHandler(LogLevel.INFO))
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ChannelPipeline pipeline = ch.pipeline();
                        pipeline.addLast(new HttpServerCodec());
                        pipeline.addLast(new HttpObjectAggregator());
                        pipeline.addLast(new TracingDispatchHandler());
                    }
                });

        channelFuture = bootstrap.bind(port).sync();
        return port;
    }

    public void stopServer() {
        if (channelFuture != null && channelFuture.isBound()) {
            channelFuture.channel().closeFuture().awaitUninterruptibly();
        }
        workerGroup.shutdownGracefully();
        bossGroup.shutdownGracefully();
    }

    private class TracingDispatchHandler extends io.netty.handler.tracing.TraceHandler {
        public TracingDispatchHandler() {
            super(new Tracer());
        }

        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            if (msg instanceof io.netty.handler.codec.http.FullHttpRequest) {
                io.netty.handler.codec.http.FullHttpRequest request = (io.netty.handler.codec.http.FullHttpRequest) msg;
                String path = request.uri();
                if ("/unsampled".equals(path)) {
                    ctx.fireChannelRead(msg);
                } else if ("/traced".equals(path)) {
                    Span span = Context.current().getSpan();
                    if (span != null) {
                        span.setAttribute("path", path);
                    }
                    ctx.fireChannelRead(msg);
                } else {
                    ctx.fireChannelRead(msg);
                }
            }
        }

        @Override
        public void write(ChannelHandlerContext ctx, Object msg, ChannelPromise promise) {
            if (msg instanceof io.netty.handler.codec.http.FullHttpResponse) {
                io.netty.handler.codec.http.FullHttpResponse response = (io.netty.handler.codec.http.FullHttpResponse) msg;
                Span span = Context.current().getSpan();
                if (span != null) {
                    span.setAttribute("response", response.status().code());
                }
            }
            super.write(ctx, msg, promise);
        }
    }
}

