            dataChannel = Files.createTempFile("flv", "tmp").toUri().toURL().openStream().getChannel();
        } catch (IOException e) {
            throw new RuntimeException("Error initializing FLV writer", e);
        }
    }

    public FLVWriter(Path path, boolean append) {
        this.filePath = path.toString();
        this.append = append;
        this.appendOffset = 0;
        this.meta = null;
        try {
            if (append) {
                fileChannel = Files.open(path, java.nio.file.StandardOpenOption.READ, java.nio.file.StandardOpenOption.WRITE);
                appendOffset = fileChannel.size();
            } else {
                fileChannel = Files.newFile(path).toUri().toURL().openStream().getChannel();
            }
            dataChannel = Files.createTempFile("flv", "tmp").toUri().toURL().openStream().getChannel();
        } catch (IOException e) {
            throw new RuntimeException("Error initializing FLV writer", e);
        }
    }

    private Map<String, ?> getMetaData(Path path, int maxTags) {
        try {
            byte[] buffer = new byte[4096];
            int bytesRead = Files.readAllBytes(path).length;
            if (bytesRead < 12) return null;
            ByteBuffer bb = ByteBuffer.wrap(buffer);
            bb.putInt(0x464C5600); // 'FLV' + 0
            bb.putInt(0); // version
            bb.putInt(0); // header size
            bb.position(8);
            int tagCount = 0;
            while (tagCount < maxTags && bb.remaining() > 0) {
                int tagType = bb.getInt();
                int dataSize = bb.getInt();
                int timestamp = bb.getInt();
                int streamId = bb.getInt();
                bb.position(bb.position() + dataSize);
                tagCount++;
            }
            return null;
        } catch (IOException e) {
            throw new RuntimeException("Error reading metadata", e);
        }
    }

    private void createDataFile() {
