        } catch (IOException e) {
            throw new RuntimeException("Error creating output file", e);
        }
    }

    public boolean writeTag(ITag tag) {
        try {
            ByteBuffer bb = ByteBuffer.allocate(tag.getSize());
            tag.write(bb);
            bb.flip();
            dataChannel.write(bb);
            return true;
        } catch (IOException e) {
            return false;
        }
    }

    public void close() {
        try {
            finalizeFlv();
            dataChannel.close();
            fileChannel.close();
        } catch (IOException e) {
            throw new RuntimeException("Error closing FLV writer", e);
        }
    }

    private long finalizeFlv() {
        try {
            if (append) {
                dataChannel.position(0);
                fileChannel.position(appendOffset);
                ByteBuffer bb = ByteBuffer.allocate(4);
                bb.putInt(0);
                bb.flip();
                fileChannel.write(bb);
                dataChannel.position(0);
                fileChannel.position(appendOffset);
                byte[] data = new byte[(int) dataChannel.size()];
                dataChannel.read(bb);
                bb.flip();
                fileChannel.write(bb);
            } else {
                dataChannel.position(0);
                fileChannel.position(0);
                byte[] data = new byte[(int) dataChannel.size()];
                dataChannel.read(bb);
