    private final String clientSecret;
    private final String redirectUri;

    public GithubProvider(String clientId, String clientSecret, String redirectUri) {
        this.clientId = clientId;
        this.clientSecret = clientSecret;
        this.redirectUri = redirectUri;
    }

    public String getAccessToken(AccessTokenDTO accessTokenDTO) throws IOException {
        String grantType = "authorization_code";
        String url = "https://github.com/login/oauth/access_token";
        FormBody formBody = new FormBody.Builder()
                .add("client_id", clientId)
                .add("client_secret", clientSecret)
                .add("code", accessTokenDTO.getCode())
                .add("redirect_uri", redirectUri)
                .add("grant_type", grantType)
                .build();

        Request request = new Request.Builder()
                .url(url)
                .post(formBody)
                .build();

        Response response = new OkHttpClient().newCall(request).execute();
        if (!response.isSuccessful()) throw new IOException("Unexpected code " + response);

        String responseBody = response.body().string();
        String[] params = responseBody.split("&");
        for (String param : params) {
            if (param.startsWith("access_token")) {
                return param.substring(param.indexOf("=") + 1);
            }
        }
        return null;
    }

    public GithubUser getUser(String accessToken) throws IOException {
        String url = "https://api.github.com/user";
        Request request = new Request.Builder()
                .url(url)
                .header("Authorization", "token " + accessToken)
                .build();

        Response response = new OkHttpClient().newCall(request).execute();
        if (!response.isSuccessful()) throw new IOException("Unexpected code " + response);

        String responseBody = response.body().string();
        return parseGithubUser(responseBody);
    }

    private GithubUser parseGithubUser(String responseBody) {
        try {
            JsonParser parser = new JsonParser();
            JsonObject jsonObject = parser.parse(responseBody).getAsJsonObject();
            return new GithubUser(
                    jsonObject.get("login").getAsString(),
                    jsonObject.get("id").getAsLong(),
