import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.util.Map;

public class Network {
    private static final HttpClient httpClient = HttpClient.newHttpClient();

    public HttpResponse<String> makePOSTRequest(String url, Map<Object, Object> parameters, Map<String, String> headers, boolean convertParametersToJSON) throws Exception {
        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .headers(headers);

        if (convertParametersToJSON) {
            String jsonBody = convertMapToJson(parameters);
            requestBuilder.header("Content-Type", "application/json");
            requestBuilder.post(HttpRequest.BodyPublishers.ofString(jsonBody));
        } else {
            String formData = buildFormDataFromMap(parameters);
            requestBuilder.header("Content-Type", "application/x-www-form-urlencoded");
            requestBuilder.post(HttpRequest.BodyPublishers.ofString(formData));
        }

        return httpClient.send(requestBuilder.build(), HttpResponse.BodyHandlers.ofString());
    }

    private String buildFormDataFromMap(Map<Object, Object> data) {
        StringBuilder formData = new StringBuilder();
        for (Map.Entry<Object, Object> entry : data.entrySet()) {
            formData.append(encode(entry.getKey())).append("=").append(encode(entry.getValue())).append("&");
        }
        return formData.substring(0, formData.length() - 1);
    }

    private String encode(Object value) {
        if (value instanceof String s) {
            return s.replace("+", "%20").replace(" ", "%20");
        }
        return value.toString();
    }

    private String convertMapToJson(Map<Object, Object> data) {
        StringBuilder jsonBuilder = new StringBuilder("{");
        boolean first = true;
        for (Map.Entry<Object, Object> entry : data.entrySet()) {
            if (!first) {
                jsonBuilder.append(",");
            }
            jsonBuilder.append(encode(entry.getKey())).append(":").append(encode(entry.getValue()));
            first = false;
        }
        jsonBuilder.append("}");
        return jsonBuilder.toString();
    }

    public JsonElement makeJsonRequest(String url) throws Exception {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .GET()
                .build();

        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());

        if (failure(response.statusCode())) {
            throw new IOException("HTTP error code: " + response.statusCode());
        }

        return JsonParser.parseString(response.body());
    }

    private boolean failure(int statusCode) {
        return statusCode < 200 || statusCode >= 300;
    }
}

