import okhttp3.*;

import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

public class Scraper {
    private final OkHttpClient client;

    public Scraper() {
        this.client = new OkHttpClient.Builder()
                .connectTimeout(10, TimeUnit.SECONDS)
                .readTimeout(10, TimeUnit.SECONDS)
                .build();
    }

    public List<String> scrape(String url, long timeoutMillis) throws IOException {
        Request request = new Request.Builder()
                .url(url)
                .build();

        return fetchData(request, timeoutMillis);
    }

    public List<String> scrape(String url, String user, String password, long timeoutMillis) throws IOException {
        String auth = "Basic " + Base64.encodeToString((user + ":" + password).getBytes(), Base64.NO_WRAP);
        Request request = new Request.Builder()
                .url(url)
                .header("Authorization", auth)
                .build();

        return fetchData(request, timeoutMillis);
    }

    private List<String> fetchData(Request request, long timeoutMillis) throws IOException {
        int retryCount = 0;
        while (retryCount < 3) {
            try {
                Response response = client.newCall(request).execute();
                if (response.isSuccessful()) {
                    return parseResponse(response.body().string());
                } else {
                    throw new IOException("Unexpected code " + response.code());
                }
            } catch (IOException e) {
                retryCount++;
                if (retryCount < 3) {
                    System.out.println("Retrying request due to error: " + e.getMessage());
                    try {
                        Thread.sleep(1000);
                    } catch (InterruptedException ignored) {}
                } else {
                    throw e;
                }
            }
        }
        return new ArrayList<>();
    }

    private List<String> parseResponse(String responseText) {
        List<String> result = new ArrayList<>();
        String[] lines = responseText.split("\n");
        for (String line : lines) {
            if (line.contains("content")) {
                result.add(line);
            }
        }
        return result;
    }
}

