            log.error("Error processing DLQ message", e);
        }
    }

    public KafkaProducer<byte[], byte[]> producer() {
        if (producer == null) {
            producer = createProducer();
        }
        return producer;
    }

    private KafkaProducer<byte[], byte[]> createProducer() {
        Map<String, Object> props = baseProducerConfigs();
        props.put("key.serializer", StringSerializer.class);
        props.put("value.serializer", Bytes.Serialize.class);
        return new KafkaProducer<>(props);
    }

    private Map<String, Object> baseProducerConfigs() {
        Map<String, Object> props = new HashMap<>();
        props.put("bootstrap.servers", configs.get("bootstrap.servers"));
        props.put("acks", "1");
        props.put("retries", 3);
        props.put("enable.idempotence", true);
        props.put("max.in.flight.requests.per.connection", 5);
        props.put("delivery.timeout.ms", 30000);
        props.put("max.block.ms", 60000);
        props.put("buffer.memory", 33554432);
        props.put("message.timeout.ms", 30000);
        props.put("max.request.size", 1048576);
        props.put("request.timeout.ms", 30000);
        props.put("fetch.max.bytes", 1048576);
        props.put("fetch.min.bytes", 1);
        props.put("fetch.wait.max.ms", 500);
        props.put("max.poll.records", 500);
        props.put("auto.offset.reset", "earliest");
        props.put("enable.auto.commit", false);
        props.put("partitioner.class", "org.apache.kafka.clients.producer.internals.DefaultPartitioner");
        return props;
    }

    private byte[] convertToBytes(RuleContext ctx, Object message) {
        if (message instanceof String) {
            return toBytes((String) message);
        } else if (message instanceof Map) {
            return convertToJsonBytes(ctx, message);
        } else {
            return toBytes(message.toString());
        }
    }

    private byte[] convertToJsonBytes(RuleContext ctx, Object message) {
        return toBytes("{\"rule\": \"" + ctx.getRuleName() + "\", \"message\": \"" + message.toString() + "\"}");
    }

    private byte[] toBytes(String value) {
        return value.getBytes(StandardCharsets.UTF_8);
    }

    private void populateHeaders(RuleContext ctx, ProducerRecord<byte[], byte[]> producerRecord, RuleException ex) {
        producerRecord.headers().add(Bytes.wrap(HEADER_PREFIX.getBytes(StandardCharsets.UTF_8)), Bytes.wrap(ctx.getRuleName().getBytes(StandardCharsets.UTF_8)));
        producerRecord.headers().add(Bytes.wrap(HEADER_PREFIX.getBytes(StandardCharsets.UTF_8)), Bytes.wrap(ctx.getRuleMode().getBytes(StandardCharsets.UTF_8)));
        producerRecord.headers().add(Bytes.wrap(HEADER_PREFIX.getBytes(StandardCharsets.UTF_8)), Bytes.wrap(ctx.getRuleSubject().getBytes(StandardCharsets.UTF_8)));
        producerRecord.headers().add(Bytes.wrap(HEADER_PREFIX.getBytes(StandardCharsets.UTF_8)), Bytes.wrap(ctx.getRuleTopic().getBytes(StandardCharsets.UTF_8)));
        producerRecord.headers().add(Bytes.wrap(HEADER_PREFIX.getBytes(StandardCharsets.UTF_8)), Bytes.wrap(ex.getMessage().getBytes(StandardCharsets.UTF_8)));
    }

    public void close() {
        if (producer != null) {
            producer.close();
        }
    }
}

