    private final CloseableHttpClient client = HttpClients.createDefault();

    private ServerSocketChannel serverSocketChannel;
    private Selector selector;
    private ExecutorService executorService;

    public void startServer(int port) throws IOException {
        serverSocketChannel = ServerSocketChannel.open();
        serverSocketChannel.configureBlocking(false);
        serverSocketChannel.bind(new InetSocketAddress(port));
        selector = Selector.open();
        serverSocketChannel.register(selector, SelectionKey.OP_ACCEPT);
        executorService = Executors.newCachedThreadPool();
    }

    private void handleAccept(SelectionKey key, Selector selector) {
        ServerSocketChannel serverSocket = (ServerSocketChannel) key.channel();
        SocketChannel clientSocket = serverSocket.accept();
        if (clientSocket != null) {
            clientSocket.configureBlocking(false);
            clientSocket.register(selector, SelectionKey.OP_READ);
        }
    }

    private void handleRead(SelectionKey key, Selector selector) {
        SocketChannel clientSocket = (SocketChannel) key.channel();
        ByteBuffer buffer = ByteBuffer.allocate(1024);
        try {
            int bytesRead = clientSocket.read(buffer);
            if (bytesRead > 0) {
                buffer.flip();
                String request = charset.decode(buffer).toString();
                buffer.clear();
                executeResponse(request, clientSocket);
            }
        } catch (IOException e) {
            try {
                clientSocket.close();
            } catch (IOException ex) {
                ex.printStackTrace();
            }
        }
    }

    private void handleWrite(SelectionKey key, Selector selector) {
        SocketChannel clientSocket = (SocketChannel) key.channel();
        ByteBuffer buffer = (ByteBuffer) key.attachment();
        try {
            while (buffer.hasRemaining()) {
                int bytesWritten = clientSocket.write(buffer);
                if (bytesWritten == 0) {
                    break;
                }
            }
            if (!buffer.hasRemaining()) {
                clientSocket.register(selector, SelectionKey.OP_READ);
                buffer.clear();
            }
        } catch (IOException e) {
            try {
                clientSocket.close();
            } catch (IOException ex) {
                ex.printStackTrace();
            }
        }
    }

