        String response = "";
        try {
            HttpGet httpGet = new HttpGet("https://www.baidu.com" + (request.contains("http://") ? "" : "/"));
            CloseableHttpResponse httpResponse = client.execute(httpGet);
            HttpEntity entity = httpResponse.getEntity();
            if (entity != null) {
                response = entity.asString(charset);
            }
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            try {
                clientSocket.write(ByteBuffer.wrap(response.getBytes(charset)));
                clientSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    public static void main(String[] args) throws IOException {
