import java.io.IOException;
import java.io.File;
import java.io.RandomAccessFile;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.Files;
import java.util.function.Consumer;

public class RandomAccessReadMemoryMappedFile {
    private MappedByteBuffer mappedByteBuffer;
    private long size;
    private FileChannel fileChannel;
    private Consumer<? super MappedByteBuffer> unmapper;
    private long position = 0;
    private boolean closed = false;

    public RandomAccessReadMemoryMappedFile(String filename) throws IOException {
        this(Files.newFile(Path.of(filename)));
    }

    public RandomAccessReadMemoryMappedFile(File file) throws IOException {
        this(file.toPath());
    }

    public RandomAccessReadMemoryMappedFile(Path path) throws IOException {
        fileChannel = FileChannel.open(path, java.nio.file.StandardOpenOption.READ);
        mappedByteBuffer = fileChannel.map(java.nio.channels.FileChannel.MapMode.READ_ONLY, 0, fileChannel.size());
        size = fileChannel.size();
        unmapper = buffer -> {
            try {
                buffer.force(); // Ensure all data is written to memory
            } catch (IOException e) {
                e.printStackTrace();
            }
            try {
                fileChannel.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        };
    }

    public void close() throws IOException {
        checkClosed();
        unmapper.accept(mappedByteBuffer);
        closed = true;
    }

    public void seek(long position) {
        checkClosed();
        this.position = position;
        if (position > size) {
            position = size;
        }
    }

    public long getPosition() {
        checkClosed();
        return position;
    }

    public int read() {
        checkClosed();
        if (isEOF()) {
            return -1;
        }
        mappedByteBuffer.position((int) position);
        int b = mappedByteBuffer.get() & 0xFF;
        position++;
        return b;
    }

    public int read(byte[] b, int offset, int length) {
        checkClosed();
        if (isEOF()) {
            return -1;
        }
        int bytesRead = 0;
        while (bytesRead < length) {
            int remaining = length - bytesRead;
            int bytesToRead = Math.min(remaining, (int) (size - position));
            if (bytesToRead <= 0) {
                break;
            }
            mappedByteBuffer.position((int) position);
            bytesRead += mappedByteBuffer.get(b, offset + bytesRead, bytesToRead);
            position += bytesToRead;
        }
        return bytesRead;
    }

    public long length() {
        checkClosed();
        return size;
    }

    public boolean isClosed() {
        return closed;
    }

    public boolean isEOF() {
        checkClosed();
        return position >= size;
    }

    private void checkClosed() throws IOException {
        if (closed) {
            throw new IOException("Reader is closed");
        }
    }
}

