    private Pattern shellWildcardPattern;

    public ShellCommand(String command) {
        this.command = command;
        this.shellWildcardPattern = Pattern.compile("([*?[])");
        this.argv = tokenize(command);
    }

    private boolean hasWildcard(String command) {
        return shellWildcardPattern.matcher(command).find();
    }

    private List<String> tokenize(String s) {
        List<String> tokens = new ArrayList<>();
        int i = 0;
        while (i < s.length()) {
            if (s.charAt(i) == ' ') {
                i++;
                continue;
            }
            if (s.charAt(i) == '\"') {
                int end = s.indexOf('"', i + 1);
                if (end != -1) {
                    tokens.add(s.substring(i + 1, end));
                    i = end + 1;
                } else {
                    tokens.add(s.substring(i));
                    i = s.length();
                }
            } else if (s.charAt(i) == '\'') {
                int end = s.indexOf('\'', i + 1);
                if (end != -1) {
                    tokens.add(s.substring(i + 1, end));
                    i = end + 1;
                } else {
                    tokens.add(s.substring(i));
                    i = s.length();
                }
            } else {
                int end = s.indexOf(' ', i);
                if (end == -1) {
                    tokens.add(s.substring(i));
                    i = s.length();
                } else {
                    tokens.add(s.substring(i, end));
                    i = end;
                }
            }
        }
        return tokens;
    }

    public void doExecute(ConcurrentTestCommandExecutor executor) {
        try {
            ProcessBuilder pb = new ProcessBuilder(argv);
