import java.nio.file.Path;
import java.nio.file.Files;
import java.nio.file.StandardOpenOption;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.locks.ReentrantLock;

public class DaemonRegistry implements AutoCloseable {
    private static final Logger LOGGER = Logger.getLogger(DaemonRegistry.class.getName());
    private static final int MAX_LENGTH = 32 * 1024; // 32KB
    private static final long LOCK_TIMEOUT_MS = 20000; // 20 seconds
    private final Map<Path, Object> locks = new ConcurrentHashMap<>();
    private final Path registryFile;
    private final Object lck = new ReentrantLock();
    private FileChannel channel;
    private MappedByteBuffer buffer;
    private long size;
    private final Map<String, DaemonInfo> infosMap = new ConcurrentHashMap<>();

    public DaemonRegistry(Path registryFile) {
        this.registryFile = registryFile;
        try {
            if (!Files.exists(registryFile)) {
                Files.createFile(registryFile);
            }
            channel = FileChannel.open(registryFile, StandardOpenOption.READ, StandardOpenOption.WRITE);
            size = Math.min(MAX_LENGTH, channel.size());
            buffer = channel.map(FileChannel.MapMode.READ_WRITE, 0, size);
            read();
        } catch (IOException e) {
            LOGGER.severe("Error initializing registry: " + e.getMessage());
        }
    }

    @Override
    public void close() throws IOException {
        if (channel != null) {
            channel.close();
        }
    }

    public List<DaemonInfo> getAll() {
        List<DaemonInfo> result = new ArrayList<>();
        result.addAll(infosMap.values());
        return result;
    }

    public void read() {
        synchronized (lck) {
            try {
                if (buffer == null || buffer.isDirty()) {
                    buffer.force();
                    buffer.clear();
                }
                int offset = 0;
                while (offset < size) {
                    String key = readString();
                    if (key == null || key.isEmpty()) {
                        break;
                    }
                    DaemonInfo info = new DaemonInfo();
                    info.setId(key);
                    info.setName(readString());
                    info.setStatus(readString());
                    info.setLastActive(readString());
                    info.setPid(readString());
                    info.setStartTime(readString());
                    info.setEndTime(readString());
                    info.setLastError(readString());
                    infosMap.put(key, info);
                    offset += 7 * 256; // 7 strings, each 256 bytes
                }
            } catch (Exception e) {
                LOGGER.severe("Error reading registry: " + e.getMessage());
            }
        }
    }

    public void doUpdate(Runnable updater) {
        synchronized (lck) {
            try {
                FileLock lock = tryLock();
                if (lock != null) {
                    try {
                        updater.run();
                    } finally {
                        lock.release();
                    }
                }
            } catch (Exception e) {
                LOGGER.severe("Error during update: " + e.getMessage());
            }
        }
    }

    public FileLock tryLock() {
        Path path = registryFile;
        Object lockKey = locks.computeIfAbsent(path, k -> new ReentrantLock());
        ReentrantLock lock = (ReentrantLock) lockKey;
        try {
            if (lock.tryLock(LOCK_TIMEOUT_MS, java.util.concurrent.TimeUnit.MILLISECONDS)) {
                return new FileLock(channel, 0, size, true);
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        } finally {
            if (!lock.isHeldByCurrentThread()) {
                lock.unlock();
            }
        }
        return null;
    }

    public void reset() {
        synchronized (lck) {
            try {
                infosMap.clear();
                buffer.clear();
                buffer.position(0);
                buffer.limit(0);
                buffer = channel.map(FileChannel.MapMode.READ_WRITE, 0, size);
            } catch (Exception e) {
                LOGGER.severe("Error resetting registry: " + e.getMessage());
            }
        }
    }

    public String readString() {
        int length = 0;
        while (length < 256 && buffer.get(length) != 0) {
            length++;
        }
        if (length == 0) {
            return null;
        }
        byte[] bytes = new byte[length];
        buffer.get(bytes);
        return new String(bytes, java.nio.charset.StandardCharsets.UTF_8);
    }

    public void writeString(String str) {
        byte[] bytes = str.getBytes(java.nio.charset.StandardCharsets.UTF_8);
        int length = bytes.length;
        while (length < 256) {
            bytes[length++] = 0;
        }
        buffer.put(bytes);
        buffer.position(buffer.position() + 256);
    }

    public long nextPowerOf2(long a, long min) {
        a = Math.max(a, min);
        a |= a - 1;
        a += 1;
        return a;
    }
}

